
public class Supplier {

	private int pizzaStock;
	private int hamburgerStock;
	private int coldBeverageStock;
	public Supplier(int pizzaStock, int hamburgerStock, int coldBeverageStock) {
		
		this.pizzaStock = pizzaStock;
		this.coldBeverageStock = coldBeverageStock;
		this.hamburgerStock = hamburgerStock;
		// TODO Auto-generated constructor stub
	}
	
	public int getPizzaStock() {
		return pizzaStock;
	}
	public void setPizzaStock(int pizzaStock) {
		this.pizzaStock = pizzaStock;
	}
	public int getHamburgerStock() {
		return hamburgerStock;
	}
	public void setHamburgerStock(int hamburgerStock) {
		this.hamburgerStock = hamburgerStock;
	}
	public int getColdBeverageStock() {
		return coldBeverageStock;
	}
	public void setColdBeverageStock(int coldBeverageStock) {
		this.coldBeverageStock = coldBeverageStock;
	}
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	

}
